mapper.select:
select member_id, password, nickname, email, role
from member
where member_Id = #{memberId}

select member_id, password, nickname, email, role
from member
where nickname = #{nickname}

select member_id, password, nickname, email, role
from member
where email = #{email}

select member_id, oauth_server_type, email
from member
where email = #{email} and oauth_server_type = #{oauthServerType}

select member_id, password, nickname, email, role
from member
where member_Id = #{memberId}
and email = #{email}

mapper.update:
update member
set password = #{password},
    nickname = #{nickname},
    image_url = #{imageUrl}
where member_id = #{memberId}
